import React, { useContext } from 'react';
import { useHistory } from "react-router";
import { CurrentUser } from './contexts/CurrentUser';
import { Link } from 'react-router-dom';
import './App.css';

function Navigation() {
  const history = useHistory();
  const { currentUser, logout } = useContext(CurrentUser);

  let loginActions = (
    <>
      <li style={{ float: 'right' }}>
        <button onClick={() => history.push("/sign-up")} className="link-button">
          Sign Up
        </button>
      </li>
      <li style={{ float: 'right' }}>
        <button onClick={() => history.push("/login")} className="link-button">
          Login
        </button>
      </li>
    </>
  );

  if (currentUser) {
    loginActions = (
      <>
        <li style={{ float: 'right' }}>
          Logged in as {currentUser.firstName} {currentUser.lastName}
        </li>
        <li style={{ float: 'right' }}>
          <button onClick={logout} className="link-button">
            Logout
          </button>
        </li>
      </>
    );
  }

  let addPlaceButton = null;

  if (currentUser?.role === 'admin') {
    addPlaceButton = (
      <li>
        <button onClick={() => history.push("/places/new")} className="link-button">
          Add Place
        </button>
      </li>
    );
  }

  return (
    <nav>
      <ul>
        <li>
          <Link to="/">Home</Link>
        </li>
        <li>
          <Link to="/apps">Apps</Link>
        </li>
        <li>
          <Link to="/websites">Websites</Link>
        </li>
        <li>
          <Link to="/photography">Photography</Link>
        </li>
        {addPlaceButton}
        {loginActions}
      </ul>
    </nav>
  );
}

export default Navigation;
