import React, { useState } from 'react';

const SiteSearch = () => {
  const [query, setQuery] = useState('');
  const [templates, setTemplates] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSearch = async () => {
    setLoading(true);
    setError(null);

    // Log the API key for debugging purposes
    console.log(process.env.REACT_APP_ENVATO_API_KEY);

    try {
      const response = await fetch(`https://api.envato.com/v1/discovery/search/search/item?term=${query}`, {
        headers: {
          'Authorization': `Bearer CL4GksVyyG2InLzQjrBNQef7h8aHEcr5` // Use the environment variable
        }
      });

      if (!response.ok) {
        throw new Error('Failed to fetch data');
      }

      const data = await response.json();
      setTemplates(data.matches); // Adjusted to the structure of Envato API response
    } catch (error) {
      setError('Error fetching data');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="search-container">
      <input
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Search Websites..."
        className="search-input"
      />
      <button className="search-button" onClick={handleSearch} disabled={loading}>
        {loading ? 'Searching...' : 'Search'}
      </button>

      {error && <p className="error-message">{error}</p>}

      <div className="template-list">
        {templates.map(template => (
          <div key={template.id} className="template-item">
            <h3>{template.name}</h3>
            <img src={template.previews.landscape_preview.landscape_url} alt={template.name} />
            <p>Category: {template.category}</p>
            {/* Display other template details as needed */}
          </div>
        ))}
      </div>
    </div>
  );
};

export default SiteSearch;
